public class Config2 { 	public string a {get;set;} 	public string abc {get;set;} 	public class ab 	{		public string abd {get;set;} 		public string abd1 {get;set;} 		public string abd13 {get;set;} 	} #region The public method        public static Config2 LoadConfig()        {            try            {                object obj = XMLConfigUtility.LoadConfigByType(typeof(Config2));            if (obj is Config2)                    return obj as Config2;                else                    return new Config2();            }            catch            {                return new Config2();            }        }        public bool SaveConfig()        {            try            {                return XMLConfigUtility.SaveNewConfig(this);            }            catch (Exception ex)            {                DevExpress.XtraEditors.XtraMessageBox.Show(ex)                return false;            }        }        #endregion }
